import React from "react";
import PropTypes from "prop-types"; // Import PropTypes for prop validation

function Note({ title, content }) { // Destructure props for better readability
    return (
        <div className="note">
            <h1>{title}</h1>
            <p>{content}</p>
        </div>
    );
}

// Adding PropTypes to validate the props
Note.propTypes = {
    title: PropTypes.string.isRequired, // title should be a string and is required
    content: PropTypes.string.isRequired // content should be a string and is required
};

export default Note;
