import React from "react";
import PropTypes from "prop-types";

function Note({ id, title, content, onDelete }) { // Adding id and onDelete props
    return (
        <div className="note">
            <h1>{title}</h1>
            <p>{content}</p>
            <button onClick={() => onDelete(id)}>DELETE</button> {/* Handling delete */}
        </div>
    );
}

// Adding PropTypes for the component props
Note.propTypes = {
    id: PropTypes.number.isRequired, // Ensure id is a number and is required
    title: PropTypes.string.isRequired, // Ensure title is a string and is required
    content: PropTypes.string.isRequired, // Ensure content is a string and is required
    onDelete: PropTypes.func.isRequired // Ensure onDelete is a function and is required
};

export default Note;
