import React, { useState } from "react";

function CreateArea(props) {
    // State to hold note data
    const [note, setNote] = useState({
        title: "",
        content: ""
    });

    // Handle change in input and textarea fields
    function handleChange(event) {
        const { name, value } = event.target;

        setNote(prevNote => ({
            ...prevNote,
            [name]: value
        }));
    }

    // Handle form submission
    function submitNote(event) {
        props.onAdd(note); // Call the onAdd function passed from the parent component
        setNote({ title: "", content: "" }); // Reset the note state to clear the form
        event.preventDefault(); // Prevent default form submission behavior
    }

    return (
        <div>
            <form onSubmit={submitNote}>
                <input 
                    name="title" 
                    value={note.title} 
                    onChange={handleChange} 
                    placeholder="Title"
                />
                
                <textarea 
                    name="content" 
                    onChange={handleChange} 
                    value={note.content} 
                    placeholder="Take a note..." 
                    rows="3"
                />

                <button type="submit">Add</button> {/* Setting button type to "submit" */}
            </form>
        </div>
    );
}

export default CreateArea;
