import React, { useState } from "react";



function CreateArea(){
    const [note, setNote] = useState({
        title : "",
        contant: ""
    });

    function handleChange(event){
         const {name, value} = event.target;

         setNote (prevNote =>{
            return {
                ...prevNote,
                [name]:value
            }
         })
    };
return <div>
    <form>
        <input name="title" 
        value={note.title} 
        onClick={handleChange}
        placeholder="title"/>
        
        <textarea 
        name="content" 
        onClick={handleChange}
        value={note.content} 
        placeholder="take a note..." 
        rows="3"/>

        <button>add</button>
    </form>
</div>
}

export default CreateArea;
